package com.itsmarts.SmartRouteTruckApp.clases;

import android.content.Context;
import android.os.Handler;
import android.view.View;
import android.view.animation.Animation;
import android.view.inputmethod.InputMethodManager;
import android.widget.AdapterView;
import android.widget.ArrayAdapter;
import android.widget.AutoCompleteTextView;
import android.widget.Button;
import android.widget.ImageButton;
import android.widget.ImageView;
import android.widget.TextView;

import androidx.constraintlayout.widget.ConstraintLayout;

import com.google.android.material.textfield.TextInputLayout;
import com.itsmarts.SmartRouteTruckApp.MainActivity;
import com.itsmarts.SmartRouteTruckApp.R;
import com.itsmarts.SmartRouteTruckApp.modelos.TruckSpec;

import java.util.ArrayList;
import java.util.List;

public class TruckConfig {
    MainActivity mainActivity;
    ConstraintLayout truck_config_content;
    View viewDetalles;
    TextView txtCamion, txtToneladas, txtAltura, txtAncho, txtLargo;
    ImageView imgTruck, imgTonelada, imgAltura, imgAncho, imgLargo;
    TextInputLayout etPeso, etAltura, etAncho, etLongitud, etSpinner;
    AutoCompleteTextView autOpciones;
    Button btnEnviar;
    ImageButton btnCerrar;
    Animation animEntrada, animSalida;
    Boolean isMenuOpen = false;
    private double toneladas, alto, ancho, largo;
    private List<TruckSpec> savedSpecs = new ArrayList<>();
    public ArrayAdapter<TruckSpec> adapterTruckSpec;

    public TruckConfig(MainActivity mainActivity) {
        this.mainActivity = mainActivity;
        inicializarComponentes();
        inicializarListeners();
        loadSpecs();
    }
    public void mostrarMenu() {

        if (!isMenuOpen) {
            isMenuOpen = true;
            truck_config_content.setVisibility(View.VISIBLE);
            mainActivity.txtNavegacion.setVisibility(View.GONE);
            mainActivity.trackCamara.setVisibility(View.GONE);
            mainActivity.llMapas.setVisibility(View.GONE);
            mainActivity.imgVelocidad.setVisibility(View.GONE);
            mainActivity.speedTextView.setVisibility(View.GONE);
            mainActivity.distanceTextView.setVisibility(View.GONE);
            mainActivity.timeTextView.setVisibility(View.GONE);
            viewDetalles.startAnimation(mainActivity.animEntrada);
            txtCamion.startAnimation(mainActivity.animEntrada);
            imgTruck.startAnimation(mainActivity.animEntrada);
            etPeso.startAnimation(mainActivity.animEntrada);
            etAltura.startAnimation(mainActivity.animEntrada);
            etAncho.startAnimation(mainActivity.animEntrada);
            etLongitud.startAnimation(mainActivity.animEntrada);
            autOpciones.startAnimation(mainActivity.animEntrada);
            imgTonelada.startAnimation(mainActivity.animEntrada);
            imgAltura.startAnimation(mainActivity.animEntrada);
            imgAncho.startAnimation(mainActivity.animEntrada);
            imgLargo.startAnimation(mainActivity.animEntrada);
            btnEnviar.startAnimation(mainActivity.animEntrada);
            btnCerrar.startAnimation(mainActivity.animEntrada);
        } else {
            isMenuOpen = false;
            viewDetalles.startAnimation(mainActivity.animSalida);
            txtCamion.startAnimation(mainActivity.animSalida);
            imgTruck.startAnimation(mainActivity.animSalida);
            etPeso.startAnimation(mainActivity.animSalida);
            etAltura.startAnimation(mainActivity.animSalida);
            etAncho.startAnimation(mainActivity.animSalida);
            etLongitud.startAnimation(mainActivity.animSalida);
            etSpinner.startAnimation(mainActivity.animSalida);
            autOpciones.startAnimation(mainActivity.animSalida);
            imgTonelada.startAnimation(mainActivity.animSalida);
            imgAltura.startAnimation(mainActivity.animSalida);
            imgAncho.startAnimation(mainActivity.animSalida);
            imgLargo.startAnimation(mainActivity.animSalida);
            btnEnviar.startAnimation(mainActivity.animSalida);
            btnCerrar.startAnimation(mainActivity.animSalida);
            mainActivity.txtNavegacion.setVisibility(View.VISIBLE);
            mainActivity.trackCamara.setVisibility(View.VISIBLE);
            mainActivity.llMapas.setVisibility(View.VISIBLE);
            mainActivity.imgVelocidad.setVisibility(View.VISIBLE);
            mainActivity.speedTextView.setVisibility(View.VISIBLE);
            mainActivity.distanceTextView.setVisibility(View.VISIBLE);
            mainActivity.timeTextView.setVisibility(View.VISIBLE);
            truck_config_content.setVisibility(View.GONE);
        }
    }
    public void inicializarComponentes() {
        truck_config_content = mainActivity.findViewById(R.id.truck_config_content);
        viewDetalles = mainActivity.findViewById(R.id.viewDetalles);
        txtCamion = mainActivity.findViewById(R.id.txtCamion);
        imgTruck = mainActivity.findViewById(R.id.imgTruck);
        etPeso = mainActivity.findViewById(R.id.etPeso);
        etAltura = mainActivity.findViewById(R.id.etAltura);
        etAncho = mainActivity.findViewById(R.id.etAncho);
        etLongitud = mainActivity.findViewById(R.id.etLongitud);
        etSpinner = mainActivity.findViewById(R.id.etSpinner);
        autOpciones = mainActivity.findViewById(R.id.autOpciones);
        imgTonelada = mainActivity.findViewById(R.id.imgTonelada);
        imgAltura = mainActivity.findViewById(R.id.imgAltura);
        imgAncho = mainActivity.findViewById(R.id.imgAncho);
        imgLargo = mainActivity.findViewById(R.id.imgLargo);
        btnEnviar = mainActivity.findViewById(R.id.btnEnviar);
        btnCerrar = mainActivity.findViewById(R.id.btnCerrar);
        txtToneladas = mainActivity.findViewById(R.id.txtToneladas);
        txtAltura = mainActivity.findViewById(R.id.txtAltura);
        txtAncho = mainActivity.findViewById(R.id.txtAncho);
        txtLargo = mainActivity.findViewById(R.id.txtLargo);
    }
    public void inicializarListeners() {
        btnCerrar.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                btnCerrar.startAnimation(mainActivity.animacionClick);
                new Handler().postDelayed(new Runnable() {
                    @Override
                    public void run() {
                        mostrarMenu();
                        InputMethodManager imm = (InputMethodManager) mainActivity.getSystemService(Context.INPUT_METHOD_SERVICE);
                        imm.hideSoftInputFromWindow(v.getWindowToken(), 0);
                    }
                }, 400);
            }
        });
        autOpciones.setOnItemClickListener(new AdapterView.OnItemClickListener() {
            @Override
            public void onItemClick(AdapterView<?> parent, View view, int position, long id) {
                TruckSpec selectedSpec = (TruckSpec) parent.getItemAtPosition(position);
                imgTruck.setImageBitmap(selectedSpec.imagen);
                txtToneladas.setText(String.valueOf(selectedSpec.toneladas));
                txtAltura.setText(String.valueOf(selectedSpec.altura));
                txtAncho.setText(String.valueOf(selectedSpec.ancho));
                txtLargo.setText(String.valueOf(selectedSpec.largo));

                setTruckSpecifications(selectedSpec.toneladas, selectedSpec.altura, selectedSpec.ancho, selectedSpec.largo);
            }
        });
        btnEnviar.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                btnEnviar.startAnimation(mainActivity.animacionClick);
                mainActivity.routingExample.setTruckSpecifications(toneladas, alto, ancho, largo);
                mostrarMenu();
            }
        });
    }
    public void setTruckSpecifications(double toneladas, double alto, double ancho, double largo) {
        this.toneladas = toneladas;
        this.alto = alto;
        this.ancho = ancho;
        this.largo = largo;
    }
    private void updateAutoCompleteAdapter() {
        adapterTruckSpec = new ArrayAdapter<>(mainActivity, android.R.layout.simple_dropdown_item_1line, savedSpecs);
        autOpciones.setAdapter(adapterTruckSpec);
    }
    private void loadSpecs() {
        savedSpecs = mainActivity.dbHelper.getAllCamiones();
        if (savedSpecs.size()==0) {
            mainActivity.dbHelper.saveCamion("Rabon",9000,258,248,735,"");
            mainActivity.dbHelper.saveCamion("Torton",15000,255,249,735,"");
            mainActivity.dbHelper.saveCamion("Trailer",28000,280,255,1600,"");
            savedSpecs = mainActivity.dbHelper.getAllCamiones();
        }
        updateAutoCompleteAdapter();
    }
}
